import React, { useCallback, useMemo, useState } from "react";
import Particles from "react-tsparticles";
import { loadFull } from "tsparticles";

export default function App() {
  const [theme, setTheme] = useState("dark");

  const toggleTheme = () => {
    setTheme((prev) => (prev === "dark" ? "light" : "dark"));
  };

  const particlesInit = useCallback(async (engine) => {
    await loadFull(engine);
  }, []);

  const particlesOptions = useMemo(() => ({
    fullScreen: { enable: false },
    background: { color: { value: theme === "dark" ? "#0f172a" : "#f9fafb" } },
    fpsLimit: 60,
    interactivity: {
      events: {
        onHover: { enable: true, mode: "repulse" },
        resize: true,
      },
      modes: {
        repulse: { distance: 100, duration: 0.4 },
      },
    },
    particles: {
      number: {
        value: 80,
        density: { enable: true, area: 800 },
      },
      color: { value: theme === "dark" ? "#ffffff" : "#1e293b" },
      links: {
        enable: true,
        color: theme === "dark" ? "#ffffff" : "#1e293b",
        distance: 150,
        opacity: 0.3,
        width: 1,
      },
      move: {
        enable: true,
        speed: 1,
        outModes: { default: "bounce" },
      },
      opacity: { value: 0.4 },
      shape: { type: "circle" },
      size: { value: { min: 1, max: 5 } },
    },
    detectRetina: true,
  }), [theme]);

  const isDark = theme === "dark";

  return (
    <div className={`relative min-h-screen overflow-hidden font-mono transition-colors duration-300 ${isDark ? "bg-[#0f172a] text-white" : "bg-[#f9fafb] text-gray-900"}`}>
      <Particles
        id="tsparticles"
        init={particlesInit}
        options={particlesOptions}
        className="pointer-events-none select-none"
        style={{
          position: "absolute",
          top: 0,
          left: 0,
          width: "100%",
          height: "100%",
          zIndex: 0,
        }}
      />

      <div className="relative z-10 flex justify-center items-center min-h-screen px-4 py-10">
        <main className={`w-full max-w-4xl rounded-lg border shadow-xl p-4 sm:p-6 md:p-10 transition-all duration-300 ${isDark ? "bg-[#0b1120] border-white/10 text-white" : "bg-white border-gray-300 text-gray-800"}`}>
          <div className="flex flex-col md:flex-row justify-between items-start gap-4">
            <div>
              <h1 className="text-2xl font-bold text-green-500">const name = "John Doe";</h1>
              <p className="text-cyan-400">// Full-Stack Web Developer | Software Engineer</p>
            </div>
            <button
              onClick={toggleTheme}
              className="px-3 py-1 bg-white text-black text-sm rounded hover:bg-gray-200 transition"
            >
              Toggle Theme
            </button>
          </div>

          {/* Contact Links */}
          <div className="flex flex-wrap gap-3 mt-4">
            <a href="mailto:you@example.com" className="flex items-center gap-2 px-3 py-1.5 bg-white/10 hover:bg-white/20 rounded text-sm transition">
              📧 Email
            </a>
            <a href="https://github.com/yourusername" target="_blank" rel="noopener noreferrer" className="flex items-center gap-2 px-3 py-1.5 bg-white/10 hover:bg-white/20 rounded text-sm transition">
              💻 GitHub
            </a>
            <a href="https://linkedin.com/in/yourusername" target="_blank" rel="noopener noreferrer" className="flex items-center gap-2 px-3 py-1.5 bg-white/10 hover:bg-white/20 rounded text-sm transition">
              🔗 LinkedIn
            </a>
            <a href="/resume.pdf" target="_blank" rel="noopener noreferrer" className="flex items-center gap-2 px-3 py-1.5 bg-white/10 hover:bg-white/20 rounded text-sm transition">
              📄 Resume
            </a>
          </div>

          {/* About */}
          <section className="mt-8">
            <h2 className="text-yellow-400">// About Me</h2>
            <p className="mt-2 text-white/80 dark:text-gray-700">
              I'm a passionate software developer with a love for building intuitive and efficient digital solutions.
              With experience in both frontend and backend technologies, I enjoy solving real-world problems through code
              and continuously exploring new tools and techniques to improve my craft.
            </p>
          </section>

          {/* Skills */}
          <section className="mt-6">
            <h2 className="text-yellow-400">// Skills</h2>
            <div className="flex flex-wrap gap-2 mt-2">
              {["React", "Node.js", "JavaScript", "TypeScript", "Tailwind CSS", "MongoDB", "Express", "Git", "Docker"].map(skill => (
                <span key={skill} className="px-2 py-1 bg-white/10 rounded text-sm">{skill}</span>
              ))}
            </div>
          </section>

          {/* Education */}
          <section className="mt-6">
            <h2 className="text-yellow-400">// Education</h2>
            <ul className="list-disc ml-6 mt-2 text-white/80 text-sm space-y-1">
              <li>B.Sc. in Computer Science, University of Techville (2018 - 2022)</li>
              <li>Certified Web Developer, FreeCodeCamp</li>
            </ul>
          </section>

          {/* Work Experience */}
          <section className="mt-6">
            <h2 className="text-yellow-400">// Work Experience</h2>
            <ul className="list-disc ml-6 mt-2 text-white/80 text-sm space-y-1">
              <li>Software Developer at CodeFactory Inc. (2023 - Present)</li>
              <li>Intern Web Developer at DevStart (2021 - 2022)</li>
            </ul>
          </section>

          {/* Projects */}
          <section className="mt-6">
            <h2 className="text-purple-400 font-semibold">const projects = [</h2>
            <div className="grid sm:grid-cols-2 md:grid-cols-3 gap-4 mt-4">
              {projects.map((proj, idx) => (
                <div
                  key={idx}
                  className="bg-white/5 p-4 rounded border border-white/10 text-sm transition transform hover:scale-[1.02] hover:shadow-lg hover:border-white/20 duration-200"
                >
                  <p>
                    <span className="text-purple-400">title</span>:{" "}
                    <span className="text-white font-semibold">"{proj.title}"</span>
                  </p>
                  <p className="text-white/70 mt-2">{proj.description}</p>
                  <a href={proj.link} target="_blank" rel="noopener noreferrer" className="text-blue-400 underline mt-2 inline-block hover:text-blue-300 transition">
                    View Project
                  </a>
                </div>
              ))}
            </div>
            <p className="text-purple-400 mt-2">];</p>
          </section>
        </main>
      </div>
    </div>
  );
}
