import React, { useEffect, useRef, useState } from "react";

const canvasSize = { width: 400, height: 400 };
const scale = 20;
const rows = canvasSize.height / scale;
const columns = canvasSize.width / scale;

const SnakeGame = () => {
  const canvasRef = useRef(null);
  const [snake, setSnake] = useState([{ x: 10, y: 10 }]);
  const [food, setFood] = useState(randomFood());
  const [dir, setDir] = useState({ x: 0, y: 0 });
  const [gameOver, setGameOver] = useState(false);

  function randomFood() {
    return {
      x: Math.floor(Math.random() * columns),
      y: Math.floor(Math.random() * rows),
    };
  }

  const resetGame = () => {
    setSnake([{ x: 10, y: 10 }]);
    setDir({ x: 0, y: 0 });
    setFood(randomFood());
    setGameOver(false);
  };

  const moveSnake = () => {
    const newSnake = [...snake];
    const head = { ...newSnake[0] };
    head.x += dir.x;
    head.y += dir.y;

    if (
      head.x < 0 || head.x >= columns ||
      head.y < 0 || head.y >= rows ||
      newSnake.some(segment => segment.x === head.x && segment.y === head.y)
    ) {
      setGameOver(true);
      return;
    }

    newSnake.unshift(head);

    if (head.x === food.x && head.y === food.y) {
      setFood(randomFood());
    } else {
      newSnake.pop();
    }

    setSnake(newSnake);
  };

  useEffect(() => {
    const context = canvasRef.current.getContext("2d");
    context.setTransform(scale, 0, 0, scale, 0, 0);

    const interval = setInterval(() => {
      if (!gameOver) {
        moveSnake();
        drawGame(context);
      }
    }, 100);

    return () => clearInterval(interval);
  });

  const drawGame = (ctx) => {
    ctx.clearRect(0, 0, columns, rows);

    // Draw food
    ctx.fillStyle = "#f43f5e";
    ctx.fillRect(food.x, food.y, 1, 1);

    // Draw snake
    ctx.fillStyle = "#10b981";
    snake.forEach(segment => ctx.fillRect(segment.x, segment.y, 1, 1));
  };

  useEffect(() => {
    const handleKey = (e) => {
      switch (e.key) {
        case "ArrowUp":
          if (dir.y !== 1) setDir({ x: 0, y: -1 });
          break;
        case "ArrowDown":
          if (dir.y !== -1) setDir({ x: 0, y: 1 });
          break;
        case "ArrowLeft":
          if (dir.x !== 1) setDir({ x: -1, y: 0 });
          break;
        case "ArrowRight":
          if (dir.x !== -1) setDir({ x: 1, y: 0 });
          break;
        default:
          break;
      }
    };
    window.addEventListener("keydown", handleKey);
    return () => window.removeEventListener("keydown", handleKey);
  }, [dir]);

  return (
    <div className="flex flex-col items-center justify-center gap-4">
      {gameOver && (
        <p className="text-red-500 font-bold text-lg">Game Over</p>
      )}

      <canvas
        ref={canvasRef}
        width={canvasSize.width}
        height={canvasSize.height}
        className="border border-slate-600 bg-black"
      />

      {gameOver && (
        <button
          onClick={resetGame}
          className="bg-slate-700 text-white px-4 py-1 rounded hover:bg-slate-600 mt-4"
        >
          Restart
        </button>
      )}
    </div>
  );
};

export default SnakeGame;
