import React, { useEffect, useRef, useState } from "react";

const canvasSize = { width: 400, height: 400 };
const scale = 20;
const rows = canvasSize.height / scale;
const columns = canvasSize.width / scale;

const SnakeGame = () => {
  const canvasRef = useRef(null);
  const ctxRef = useRef(null);
  const snakeRef = useRef([
    { x: 10, y: 10 },
    { x: 9, y: 10 },
    { x: 8, y: 10 },
  ]);
  const dirRef = useRef({ x: 1, y: 0 });
  const foodRef = useRef(randomFood());
  const intervalRef = useRef(null);

  const [gameOver, setGameOver] = useState(false);
  const [score, setScore] = useState(0); // ðŸ§® Score state

  function randomFood() {
    return {
      x: Math.floor(Math.random() * columns),
      y: Math.floor(Math.random() * rows),
    };
  }

  const resetGame = () => {
    snakeRef.current = [
      { x: 10, y: 10 },
      { x: 9, y: 10 },
      { x: 8, y: 10 },
    ];
    dirRef.current = { x: 1, y: 0 };
    foodRef.current = randomFood();
    setScore(0);
    setGameOver(false);
  };

  const getSpeed = () => {
    const length = snakeRef.current.length;

    if (length < 8) return 200;
    if (length < 12) return 180;
    if (length < 16) return 160;
    if (length < 20) return 140;
    if (length < 25) return 120;
    return 100;
  };

  const moveSnake = () => {
    const snake = [...snakeRef.current];
    const head = { ...snake[0] };
    head.x += dirRef.current.x;
    head.y += dirRef.current.y;

    const hitWall = head.x < 0 || head.x >= columns || head.y < 0 || head.y >= rows;
    const hitSelf = snake.some(segment => segment.x === head.x && segment.y === head.y);

    if (hitWall || hitSelf) {
      setGameOver(true);
      clearInterval(intervalRef.current);
      return;
    }

    snake.unshift(head);

    if (head.x === foodRef.current.x && head.y === foodRef.current.y) {
      foodRef.current = randomFood();
      setScore(snake.length - 3); // update score
    } else {
      snake.pop();
    }

    snakeRef.current = snake;
    drawGame(ctxRef.current);

    clearInterval(intervalRef.current);
    intervalRef.current = setInterval(moveSnake, getSpeed());
  };

  const drawGame = (ctx) => {
    ctx.clearRect(0, 0, columns, rows);

    // Food
    ctx.fillStyle = "#f43f5e";
    ctx.fillRect(foodRef.current.x, foodRef.current.y, 1, 1);

    // Snake
    ctx.fillStyle = "#10b981";
    snakeRef.current.forEach(segment => {
      ctx.fillRect(segment.x, segment.y, 1, 1);
    });
  };

  useEffect(() => {
    const ctx = canvasRef.current.getContext("2d");
    ctx.setTransform(scale, 0, 0, scale, 0, 0);
    ctxRef.current = ctx;

    drawGame(ctx);

    if (!gameOver) {
      intervalRef.current = setInterval(moveSnake, getSpeed());
    }

    return () => clearInterval(intervalRef.current);
  }, [gameOver]);

  useEffect(() => {
    const handleKey = (e) => {
      const key = e.key.toLowerCase();
      const { x, y } = dirRef.current;

      switch (key) {
        case "arrowup":
        case "w":
          if (y !== 1) dirRef.current = { x: 0, y: -1 };
          break;
        case "arrowdown":
        case "s":
          if (y !== -1) dirRef.current = { x: 0, y: 1 };
          break;
        case "arrowleft":
        case "a":
          if (x !== 1) dirRef.current = { x: -1, y: 0 };
          break;
        case "arrowright":
        case "d":
          if (x !== -1) dirRef.current = { x: 1, y: 0 };
          break;
        default:
          break;
      }
    };

    window.addEventListener("keydown", handleKey);
    return () => window.removeEventListener("keydown", handleKey);
  }, []);

  return (
    <div className="flex flex-col items-center justify-center gap-4 pb-6">
      {/* Score */}
      <p className="text-sm text-gray-400">Score: {score}</p>

      {/* Game Over Text */}
      {gameOver && (
        <p className="text-red-500 font-bold text-lg">Game Over â€” Final Score: {score}</p>
      )}

      {/* Canvas */}
      <canvas
        ref={canvasRef}
        width={canvasSize.width}
        height={canvasSize.height}
        style={{ imageRendering: "pixelated" }}
        className="border border-slate-600 bg-black"
      />

      {/* Restart */}
      {gameOver && (
        <button
          onClick={resetGame}
          className="bg-slate-700 text-white px-4 py-1 rounded hover:bg-slate-600"
        >
          Restart
        </button>
      )}

      {/* Game Description */}
      <div className="mt-6 max-w-md text-sm text-center text-gray-400 px-4">
        <p className="font-semibold text-white mb-1">About Snake</p>
        <p>
          Navigate the snake to eat food and grow longer. Avoid running into the walls or yourself.
        </p>
        <p className="mt-2">
          <span className="text-white font-semibold">Controls:</span> Arrow keys or WASD
        </p>
        <p>
          <span className="text-white font-semibold">Objective:</span> Grow the longest snake you can!
        </p>
      </div>
    </div>
  );
};

export default SnakeGame;
