import React, { useCallback } from "react";
import Particles from "react-tsparticles";
import { loadSlim } from "tsparticles-slim";

export default function App() {
  const particlesInit = useCallback(async (engine) => {
    await loadSlim(engine);
  }, []);

  return (
    <div className="relative min-h-screen bg-[#0f172a] text-white overflow-hidden">
      {/* Working Particles */}
      <Particles
        id="tsparticles"
        init={particlesInit}
        className="absolute inset-0 h-full w-full -z-10"
        options={{
          fullScreen: { enable: false },
          background: { color: { value: "#0f172a" } },
          fpsLimit: 60,
          interactivity: {
            events: {
              onHover: { enable: true, mode: "repulse" },
              resize: true,
            },
            modes: {
              repulse: { distance: 100, duration: 0.4 },
            },
          },
          particles: {
            number: {
              value: 60,
              density: { enable: true, area: 800 },
            },
            color: { value: "#ffffff" },
            links: {
              enable: true,
              color: "#ffffff",
              distance: 150,
              opacity: 0.3,
              width: 1,
            },
            move: {
              enable: true,
              speed: 1,
              direction: "none",
              outModes: { default: "bounce" },
            },
            opacity: { value: 0.5 },
            shape: { type: "circle" },
            size: { value: { min: 1, max: 5 } },
          },
          detectRetina: true,
        }}
      />

      {/* Content */}
      <div className="relative z-10 flex items-center justify-center min-h-screen flex-col text-center px-4">
        <h1 className="text-5xl font-bold mb-4">Particles Now Working ðŸŽ‰</h1>
        <p className="text-xl">If you see them, your setup is good!</p>
      </div>
    </div>
  );
}
