import React, { useRef, useEffect, useState } from "react";

const canvasWidth = 600;
const canvasHeight = 400;
const paddleWidth = 10;
const paddleHeight = 60;
const ballSize = 10;
const playerSpeed = 5;
const aiSpeed = 3;

const PongGame = () => {
  const canvasRef = useRef(null);
  const [playerY, setPlayerY] = useState(canvasHeight / 2 - paddleHeight / 2);
  const [aiY, setAiY] = useState(canvasHeight / 2 - paddleHeight / 2);
  const ballRef = useRef({ x: canvasWidth / 2, y: canvasHeight / 2, vx: 3, vy: 2 });
  const [playerScore, setPlayerScore] = useState(0);
  const [aiScore, setAiScore] = useState(0);

  const moveBall = () => {
    const ball = ballRef.current;

    // Move ball
    ball.x += ball.vx;
    ball.y += ball.vy;

    // Bounce top/bottom
    if (ball.y <= 0 || ball.y + ballSize >= canvasHeight) {
      ball.vy *= -1;
    }

    // Bounce off player paddle
    if (
      ball.x <= paddleWidth &&
      ball.y + ballSize >= playerY &&
      ball.y <= playerY + paddleHeight
    ) {
      ball.vx *= -1;
    }

    // Bounce off AI paddle
    if (
      ball.x + ballSize >= canvasWidth - paddleWidth &&
      ball.y + ballSize >= aiY &&
      ball.y <= aiY + paddleHeight
    ) {
      ball.vx *= -1;
    }

    // Score: AI
    if (ball.x < 0) {
      setAiScore(prev => prev + 1);
      resetBall();
    }

    // Score: Player
    if (ball.x > canvasWidth) {
      setPlayerScore(prev => prev + 1);
      resetBall();
    }
  };

  const resetBall = () => {
    ballRef.current = {
      x: canvasWidth / 2,
      y: canvasHeight / 2,
      vx: Math.random() > 0.5 ? 3 : -3,
      vy: (Math.random() - 0.5) * 4,
    };
  };

  const moveAI = () => {
    const targetY = ballRef.current.y - paddleHeight / 2;
    if (aiY < targetY) setAiY(prev => Math.min(prev + aiSpeed, canvasHeight - paddleHeight));
    else setAiY(prev => Math.max(prev - aiSpeed, 0));
  };

  const draw = (ctx) => {
    ctx.clearRect(0, 0, canvasWidth, canvasHeight);

    // Ball
    const ball = ballRef.current;
    ctx.fillStyle = "white";
    ctx.fillRect(ball.x, ball.y, ballSize, ballSize);

    // Player paddle
    ctx.fillStyle = "#10b981";
    ctx.fillRect(0, playerY, paddleWidth, paddleHeight);

    // AI paddle
    ctx.fillStyle = "#f43f5e";
    ctx.fillRect(canvasWidth - paddleWidth, aiY, paddleWidth, paddleHeight);

    // Score
    ctx.fillStyle = "#fff";
    ctx.font = "16px monospace";
    ctx.fillText(`You: ${playerScore}`, 20, 20);
    ctx.fillText(`AI: ${aiScore}`, canvasWidth - 80, 20);
  };

  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext("2d");

    const loop = () => {
      moveBall();
      moveAI();
      draw(ctx);
      requestAnimationFrame(loop);
    };

    loop();

    const handleKey = (e) => {
      const key = e.key.toLowerCase();
      setPlayerY(prev => {
        if (key === "w" || key === "arrowup") return Math.max(prev - playerSpeed, 0);
        if (key === "s" || key === "arrowdown") return Math.min(prev + playerSpeed, canvasHeight - paddleHeight);
        return prev;
      });
    };

    window.addEventListener("keydown", handleKey);
    return () => window.removeEventListener("keydown", handleKey);
  }, [playerY, aiY]);

  return (
    <div className="flex flex-col items-center w-full px-4 pb-10 gap-y-6">
      <canvas
        ref={canvasRef}
        width={canvasWidth}
        height={canvasHeight}
        className="border border-slate-700 bg-black"
        style={{ imageRendering: "pixelated" }}
      />
    </div>
  );
};

export default PongGame;
