import React, { useEffect, useRef, useState } from "react";

const canvasSize = { width: 400, height: 400 };
const scale = 20;
const rows = canvasSize.height / scale;
const columns = canvasSize.width / scale;

const SnakeGame = () => {
  const canvasRef = useRef(null);
  const snakeRef = useRef([
    { x: 10, y: 10 },
    { x: 9, y: 10 },
    { x: 8, y: 10 },
  ]);
  const dirRef = useRef({ x: 1, y: 0 });
  const foodRef = useRef(randomFood());
  const [gameOver, setGameOver] = useState(false);
  const intervalRef = useRef(null);

  function randomFood() {
    return {
      x: Math.floor(Math.random() * columns),
      y: Math.floor(Math.random() * rows),
    };
  }

  const resetGame = () => {
    snakeRef.current = [
      { x: 10, y: 10 },
      { x: 9, y: 10 },
      { x: 8, y: 10 },
    ];
    dirRef.current = { x: 1, y: 0 };
    foodRef.current = randomFood();
    setGameOver(false);
  };

  const moveSnake = () => {
    const snake = [...snakeRef.current];
    const head = { ...snake[0] };
    head.x += dirRef.current.x;
    head.y += dirRef.current.y;

    const hitWall = head.x < 0 || head.x >= columns || head.y < 0 || head.y >= rows;
    const hitSelf = snake.some(segment => segment.x === head.x && segment.y === head.y);

    if (hitWall || hitSelf) {
      setGameOver(true);
      clearInterval(intervalRef.current);
      return;
    }

    snake.unshift(head);

    if (head.x === foodRef.current.x && head.y === foodRef.current.y) {
      foodRef.current = randomFood();
    } else {
      snake.pop();
    }

    snakeRef.current = snake;
  };

  const drawGame = (ctx) => {
    ctx.clearRect(0, 0, columns, rows);

    // Draw food
    ctx.fillStyle = "#f43f5e";
    ctx.fillRect(foodRef.current.x, foodRef.current.y, 1, 1);

    // Draw snake
    ctx.fillStyle = "#10b981";
    snakeRef.current.forEach(segment => {
      ctx.fillRect(segment.x, segment.y, 1, 1);
    });
  };

  useEffect(() => {
    const ctx = canvasRef.current.getContext("2d");
    ctx.setTransform(scale, 0, 0, scale, 0, 0);

    const gameLoop = () => {
      moveSnake();
      drawGame(ctx);
    };

    if (!gameOver) {
      intervalRef.current = setInterval(gameLoop, 150); // faster tick
    }

    return () => clearInterval(intervalRef.current);
  }, [gameOver]);

  useEffect(() => {
    const handleKey = (e) => {
      const { x, y } = dirRef.current;
      switch (e.key) {
        case "ArrowUp":
          if (y !== 1) dirRef.current = { x: 0, y: -1 };
          break;
        case "ArrowDown":
          if (y !== -1) dirRef.current = { x: 0, y: 1 };
          break;
        case "ArrowLeft":
          if (x !== 1) dirRef.current = { x: -1, y: 0 };
          break;
        case "ArrowRight":
          if (x !== -1) dirRef.current = { x: 1, y: 0 };
          break;
        default:
          break;
      }
    };

    window.addEventListener("keydown", handleKey);
    return () => window.removeEventListener("keydown", handleKey);
  }, []);

  return (
    <div className="flex flex-col items-center justify-center gap-4">
      {gameOver && (
        <p className="text-red-500 font-bold text-lg">Game Over</p>
      )}

      <canvas
        ref={canvasRef}
        width={canvasSize.width}
        height={canvasSize.height}
        className="border border-slate-600 bg-black"
      />

      {gameOver && (
        <button
          onClick={resetGame}
          className="bg-slate-700 text-white px-4 py-1 rounded hover:bg-slate-600 mt-4"
        >
          Restart
        </button>
      )}
    </div>
  );
};

export default SnakeGame;
