import React, { useRef, useEffect, useState } from "react";

const COLS = 10;
const ROWS = 20;
const BLOCK_SIZE = 30;
const canvasWidth = COLS * BLOCK_SIZE;
const canvasHeight = ROWS * BLOCK_SIZE;

const TetrisGame = ({ isDark }) => {
  const canvasRef = useRef(null);

  const [grid, setGrid] = useState(
    Array.from({ length: ROWS }, () => Array(COLS).fill(0))
  );

  const [isRunning, setIsRunning] = useState(false);
  const [isPaused, setIsPaused] = useState(false);

  const [activeBlock, setActiveBlock] = useState({ x: 4, y: 0 });

  const drawGrid = (ctx) => {
    ctx.clearRect(0, 0, canvasWidth, canvasHeight);

    // Draw placed blocks
    for (let y = 0; y < ROWS; y++) {
      for (let x = 0; x < COLS; x++) {
        if (grid[y][x]) {
          ctx.fillStyle = "#38bdf8";
          ctx.fillRect(x * BLOCK_SIZE, y * BLOCK_SIZE, BLOCK_SIZE, BLOCK_SIZE);
          ctx.strokeStyle = "#1e3a8a";
          ctx.strokeRect(x * BLOCK_SIZE, y * BLOCK_SIZE, BLOCK_SIZE, BLOCK_SIZE);
        }
      }
    }

    // Draw current falling block
    if (isRunning && !isPaused) {
      ctx.fillStyle = "#f43f5e";
      ctx.fillRect(
        activeBlock.x * BLOCK_SIZE,
        activeBlock.y * BLOCK_SIZE,
        BLOCK_SIZE,
        BLOCK_SIZE
      );
    }
  };

  // Main game loop
  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext("2d");

    let lastTime = 0;
    let dropInterval = 500;
    let dropCounter = 0;

    const update = (time = 0) => {
      const deltaTime = time - lastTime;
      lastTime = time;
      dropCounter += deltaTime;

      if (isRunning && !isPaused) {
        if (dropCounter > dropInterval) {
          setActiveBlock((prev) => {
            const newY = prev.y + 1;
            return { ...prev, y: newY };
          });
          dropCounter = 0;
        }
      }

      drawGrid(ctx);
      requestAnimationFrame(update);
    };

    requestAnimationFrame(update);
  }, [isRunning, isPaused, activeBlock, grid]);

  // WASD + Arrow key movement
  useEffect(() => {
    const handleKey = (e) => {
      if (!isRunning || isPaused) return;

      const key = e.key.toLowerCase();
      setActiveBlock((prev) => {
        let newX = prev.x;
        let newY = prev.y;

        if (key === "arrowleft" || key === "a") newX--;
        if (key === "arrowright" || key === "d") newX++;
        if (key === "arrowdown" || key === "s") newY++;
        if (key === "arrowup" || key === "w") {
          // Rotation stub (to implement later)
          return prev;
        }

        return { ...prev, x: newX, y: newY };
      });
    };

    window.addEventListener("keydown", handleKey);
    return () => window.removeEventListener("keydown", handleKey);
  }, [isRunning, isPaused]);

  const handleStart = () => {
    setIsRunning(true);
    setIsPaused(false);
    setActiveBlock({ x: 4, y: 0 });
  };

  const handlePause = () => {
    if (isRunning) {
      setIsPaused((prev) => !prev);
    }
  };

  return (
    <div className="flex flex-col items-center w-full px-4 pb-10 gap-y-4">
      {/* Controls */}
      <div className="flex gap-4">
        <button
          onClick={handleStart}
          className={`px-3 py-1 rounded text-sm font-semibold ${
            isDark ? "bg-green-600 text-white" : "bg-green-200 text-black"
          }`}
        >
          Start
        </button>
        <button
          onClick={handlePause}
          className={`px-3 py-1 rounded text-sm font-semibold ${
            isDark ? "bg-yellow-600 text-white" : "bg-yellow-200 text-black"
          }`}
          disabled={!isRunning}
        >
          {isPaused ? "Resume" : "Pause"}
        </button>
      </div>

      {/* Game Canvas */}
      <canvas
        ref={canvasRef}
        width={canvasWidth}
        height={canvasHeight}
        className="border border-slate-700 bg-black"
        style={{ imageRendering: "pixelated" }}
      />

      {/* Info Box */}
      <div className={`max-w-md mt-4 p-4 rounded-md text-sm shadow transition-colors ${
        isDark ? "bg-slate-800 text-slate-200" : "bg-slate-100 text-slate-800"
      }`}>
        <p className={`${isDark ? "text-emerald-400" : "text-emerald-600"} font-semibold`}>About Tetris</p>
        <p>
          Tetris is a puzzle game where blocks (tetrominoes) fall from the top. Your goal is to move and rotate them
          to create full rows, which clear and score points!
        </p>
        <p>
          <span className={`${isDark ? "text-white" : "text-black"} font-semibold`}>Controls:</span>{" "}
          Use <span className="font-mono">W</span>/<span className="font-mono">A</span>/<span className="font-mono">S</span>/<span className="font-mono">D</span> or arrow keys to move. Down to drop faster.
        </p>
        <p>
          <span className={`${isDark ? "text-white" : "text-black"} font-semibold`}>Goal:</span>{" "}
          Clear as many lines as possible!
        </p>
      </div>
    </div>
  );
};

export default TetrisGame;
