import React, { useRef, useEffect, useState } from "react";

const canvasWidth = 600;
const canvasHeight = 400;
const paddleWidth = 10;
const paddleHeight = 60;
const ballSize = 10;
const playerSpeed = 5;

const PongGame = () => {
  const canvasRef = useRef(null);
  const playerYRef = useRef(canvasHeight / 2 - paddleHeight / 2);
  const aiYRef = useRef(canvasHeight / 2 - paddleHeight / 2);
  const ballRef = useRef({ x: canvasWidth / 2, y: canvasHeight / 2, vx: 2, vy: 1.5 });

  const playerScoreRef = useRef(0);
  const aiScoreRef = useRef(0);
  const lastTimeRef = useRef(0);
  const frameRate = 1000 / 60;

  const [difficulty, setDifficulty] = useState("easy"); // "easy" or "hard"

  const resetBall = () => {
    ballRef.current = {
      x: canvasWidth / 2,
      y: canvasHeight / 2,
      vx: Math.random() > 0.5 ? 2 : -2,
      vy: (Math.random() - 0.5) * 3,
    };
  };

  const moveBall = () => {
    const ball = ballRef.current;
    ball.x += ball.vx;
    ball.y += ball.vy;

    if (ball.y <= 0 || ball.y + ballSize >= canvasHeight) {
      ball.vy *= -1;
    }

    const playerY = playerYRef.current;
    const aiY = aiYRef.current;

    if (ball.x + ballSize < 0) {
      aiScoreRef.current += 1;
      resetBall();
      return;
    }

    if (ball.x > canvasWidth) {
      playerScoreRef.current += 1;
      resetBall();
      return;
    }

    if (
      ball.x <= paddleWidth &&
      ball.y + ballSize >= playerY &&
      ball.y <= playerY + paddleHeight
    ) {
      ball.vx *= -1;
      ball.x = paddleWidth;
    }

    if (
      ball.x + ballSize >= canvasWidth - paddleWidth &&
      ball.y + ballSize >= aiY &&
      ball.y <= aiY + paddleHeight
    ) {
      ball.vx *= -1;
      ball.x = canvasWidth - paddleWidth - ballSize;
    }
  };

  const moveAI = () => {
    const ballY = ballRef.current.y;
    const targetY = ballY - paddleHeight / 2;
    let aiSpeed = difficulty === "hard" ? 4.5 : 2.5;

    // Easy mode: only follow the ball when it's coming toward AI
    if (difficulty === "easy" && ballRef.current.vx > 0) {
      if (aiYRef.current < targetY) {
        aiYRef.current = Math.min(aiYRef.current + aiSpeed, canvasHeight - paddleHeight);
      } else {
        aiYRef.current = Math.max(aiYRef.current - aiSpeed, 0);
      }
    }

    // Hard mode: always track the ball tightly
    if (difficulty === "hard") {
      if (aiYRef.current < targetY) {
        aiYRef.current = Math.min(aiYRef.current + aiSpeed, canvasHeight - paddleHeight);
      } else {
        aiYRef.current = Math.max(aiYRef.current - aiSpeed, 0);
      }
    }
  };

  const draw = (ctx) => {
    const ball = ballRef.current;
    ctx.clearRect(0, 0, canvasWidth, canvasHeight);

    ctx.fillStyle = "white";
    ctx.fillRect(ball.x, ball.y, ballSize, ballSize);

    ctx.fillStyle = "#10b981";
    ctx.fillRect(0, playerYRef.current, paddleWidth, paddleHeight);

    ctx.fillStyle = "#f43f5e";
    ctx.fillRect(canvasWidth - paddleWidth, aiYRef.current, paddleWidth, paddleHeight);

    ctx.fillStyle = "#fff";
    ctx.font = "16px monospace";
    ctx.fillText(`You: ${playerScoreRef.current}`, 20, 20);
    ctx.fillText(`AI: ${aiScoreRef.current}`, canvasWidth - 80, 20);
    ctx.fillText(`Mode: ${difficulty.toUpperCase()}`, canvasWidth / 2 - 40, 20);
  };

  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext("2d");

    const loop = (timestamp) => {
      if (timestamp - lastTimeRef.current >= frameRate) {
        moveBall();
        moveAI();
        draw(ctx);
        lastTimeRef.current = timestamp;
      }
      requestAnimationFrame(loop);
    };

    requestAnimationFrame(loop);

    const handleKey = (e) => {
      const key = e.key.toLowerCase();
      if (key === "w" || key === "arrowup") {
        playerYRef.current = Math.max(playerYRef.current - playerSpeed, 0);
      }
      if (key === "s" || key === "arrowdown") {
        playerYRef.current = Math.min(playerYRef.current + playerSpeed, canvasHeight - paddleHeight);
      }
    };

    window.addEventListener("keydown", handleKey);
    return () => window.removeEventListener("keydown", handleKey);
  }, [difficulty]);

  return (
    <div className="flex flex-col items-center w-full px-4 pb-10 gap-y-4">
      {/* Difficulty Buttons */}
      <div className="flex gap-4">
        <button
          onClick={() => setDifficulty("easy")}
          className={`px-4 py-2 rounded-md border text-white ${
            difficulty === "easy" ? "bg-green-600" : "bg-slate-600"
          }`}
        >
          Easy Mode
        </button>
        <button
          onClick={() => setDifficulty("hard")}
          className={`px-4 py-2 rounded-md border text-white ${
            difficulty === "hard" ? "bg-red-600" : "bg-slate-600"
          }`}
        >
          Hard Mode
        </button>
      </div>

      <canvas
        ref={canvasRef}
        width={canvasWidth}
        height={canvasHeight}
        className="border border-slate-700 bg-black"
        style={{ imageRendering: "pixelated" }}
      />
    </div>
  );
};

export default PongGame;
