import React, { useState, useEffect, useCallback } from "react";
import { Mail, Github, Linkedin, Download } from "lucide-react";
import { motion } from "framer-motion";
import Particles from "react-tsparticles";
import { loadFull } from "tsparticles";

const projects = [
  {
    title: "Personal Portfolio Website",
    description: "A responsive portfolio site built with React and Tailwind CSS to showcase my projects and skills.",
    link: "#",
  },
  {
    title: "Task Manager App",
    description: "A full-stack task manager using Node.js, Express, and MongoDB with authentication and real-time updates.",
    link: "#",
  },
  {
    title: "E-commerce Platform",
    description: "A scalable e-commerce site using Next.js and Stripe for payments, with CMS integration for product management.",
    link: "#",
  },
];

const skills = ["React", "Node.js", "JavaScript", "TypeScript", "Tailwind CSS", "MongoDB", "Express", "Git", "Docker"];

export default function App() {
  const [theme, setTheme] = useState("dark");

  useEffect(() => {
    const stored = localStorage.getItem("theme");
    if (stored) setTheme(stored);
  }, []);

  useEffect(() => {
    localStorage.setItem("theme", theme);
  }, [theme]);

  const isDark = theme === "dark";
  const toggleTheme = () => setTheme(isDark ? "light" : "dark");

  const particlesInit = useCallback(async (engine) => {
    await loadFull(engine);
  }, []);

  const themeClasses = isDark
    ? "bg-gray-900 text-gray-100 border-gray-700 bg-gray-800 text-gray-300"
    : "bg-gray-100 text-gray-900 border-gray-300 bg-white text-gray-800";

  return (
    <div className={`relative min-h-screen font-mono overflow-hidden ${isDark ? "bg-gray-900 text-gray-100" : "bg-white text-gray-900"}`}>
      {/* Particle background */}
      <Particles
        id="tsparticles"
        init={particlesInit}
        className="absolute inset-0 w-full h-full -z-10 pointer-events-none"
        options={{
          fullScreen: { enable: false },
          background: { color: { value: isDark ? "#0f172a" : "#ffffff" } },
          fpsLimit: 60,
          interactivity: {
            events: { onHover: { enable: true, mode: "repulse" }, resize: true },
            modes: { repulse: { distance: 100, duration: 0.4 } },
          },
          particles: {
            number: { value: 70, density: { enable: true, area: 800 } },
            color: { value: isDark ? "#ffffff" : "#000000" },
            links: {
              color: isDark ? "#ffffff" : "#000000",
              distance: 150,
              enable: true,
              opacity: 0.3,
              width: 1,
            },
            collisions: { enable: false },
            move: {
              direction: "none",
              enable: true,
              outModes: { default: "bounce" },
              random: false,
              speed: 1,
              straight: false,
            },
            opacity: { value: 0.4 },
            shape: { type: "circle" },
            size: { value: { min: 1, max: 5 } },
          },
          detectRetina: true,
        }}
      />

      {/* Main content */}
      <div className="relative z-10 p-4">
        <div className={`max-w-6xl mx-auto border rounded-lg overflow-hidden ${isDark ? "border-gray-700" : "border-gray-300"}`}>
          <div className={`px-4 py-2 flex items-center gap-2 ${isDark ? "bg-gray-800" : "bg-gray-200"}`}>
            <span className="w-3 h-3 bg-red-500 rounded-full"></span>
            <span className="w-3 h-3 bg-yellow-500 rounded-full"></span>
            <span className="w-3 h-3 bg-green-500 rounded-full"></span>
            <span className="ml-4 text-sm text-gray-400">portfolio.jsx</span>
            <button
              onClick={toggleTheme}
              className={`ml-auto px-4 py-1 rounded-md font-semibold shadow-sm border text-sm transition-all duration-200 ${isDark ? "bg-gray-100 text-gray-900 border-gray-300 hover:bg-white" : "bg-gray-900 text-gray-100 border-gray-800 hover:bg-gray-800"}`}
            >
              Toggle Theme
            </button>
          </div>

          <div className="p-6">
            <header className="mb-12">
              <h1 className={`text-4xl font-bold ${isDark ? "text-green-400" : "text-green-700"}`}>const name = "John Doe";</h1>
              <p className={`text-lg mt-2 ${isDark ? "text-blue-400" : "text-blue-700"}`}>// Full-Stack Web Developer | Software Engineer</p>
              <div className="flex flex-wrap gap-4 mt-6">
                <button className={themeClasses}><Mail className="mr-2 w-4 h-4" />Email</button>
                <button className={themeClasses}><Github className="mr-2 w-4 h-4" />GitHub</button>
                <button className={themeClasses}><Linkedin className="mr-2 w-4 h-4" />LinkedIn</button>
                <button className={themeClasses}><Download className="mr-2 w-4 h-4" />Resume</button>
              </div>
            </header>

            <section className="mb-12">
              <h2 className={`text-2xl font-semibold mb-4 ${isDark ? "text-yellow-400" : "text-yellow-600"}`}>// About Me</h2>
              <p className={`${isDark ? "text-gray-300" : "text-gray-800"} leading-relaxed`}>
                I'm a passionate software developer with a love for building intuitive and efficient digital solutions. With experience in both frontend and backend technologies, I enjoy solving real-world problems through code and continuously exploring new tools and techniques to improve my craft.
              </p>
            </section>

            <section className="mb-12">
              <h2 className={`text-2xl font-semibold mb-4 ${isDark ? "text-yellow-400" : "text-yellow-600"}`}>// Skills</h2>
              <div className="flex flex-wrap gap-3">
                {skills.map((skill, index) => (
                  <span key={index} className={`px-3 py-1 rounded-full text-sm font-medium border ${isDark ? "border-gray-600 text-gray-200" : "border-gray-400 text-gray-800"}`}>{skill}</span>
                ))}
              </div>
            </section>

            <section className="mb-12">
              <h2 className={`text-2xl font-semibold mb-4 ${isDark ? "text-yellow-400" : "text-yellow-600"}`}>// Education</h2>
              <ul className={`${isDark ? "text-gray-300" : "text-gray-800"} list-disc list-inside`}>
                <li>B.Sc. in Computer Science, University of Techville (2018 - 2022)</li>
                <li>Certified Web Developer, FreeCodeCamp</li>
              </ul>
            </section>

            <section className="mb-12">
              <h2 className={`text-2xl font-semibold mb-4 ${isDark ? "text-yellow-400" : "text-yellow-600"}`}>// Work Experience</h2>
              <ul className={`${isDark ? "text-gray-300" : "text-gray-800"} list-disc list-inside`}>
                <li>Software Developer at CodeFactory Inc. (2023 - Present)</li>
                <li>Intern Web Developer at DevStart (2021 - 2022)</li>
              </ul>
            </section>

            <section>
              <h2 className={`text-2xl font-semibold mb-6 ${isDark ? "text-purple-400" : "text-purple-700"}`}>const projects = [</h2>
              <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-6">
                {projects.map((project, index) => (
                  <motion.div
                    key={index}
                    whileHover={{ scale: 1.02 }}
                    className={`rounded-xl p-4 shadow-md border ${isDark ? "bg-gray-800 border-gray-700 text-gray-300" : "bg-white border-gray-300 text-gray-800"}`}
                  >
                    <h3 className={`text-lg font-bold ${isDark ? "text-indigo-400" : "text-indigo-700"}`}>{`{ title: "${project.title}" }`}</h3>
                    <p className="text-sm mt-2 mb-4">{project.description}</p>
                    <a href={project.link} className={`${isDark ? "text-blue-400" : "text-blue-600"} text-sm hover:underline`}>View Project</a>
                  </motion.div>
                ))}
              </div>
              <p className={`${isDark ? "text-purple-400" : "text-purple-700"} mt-6`}>];</p>
            </section>
          </div>
        </div>
      </div>
    </div>
  );
}
